// API Execution with real APIs
const input = $input.first().json;
const intent = input.intent;
const action = input.action;

let apiResult = {};
let finalResponse = input.response;

// Execute different APIs based on intent
switch (intent) {
  case 'video_creation':
    // Real D-ID API result
    if (input.id) {
      apiResult = {
        video_url: `https://api.d-id.com/talks/${input.id}`,
        status: 'processing',
        cost: 'free',
        duration: '15s'
      };
      finalResponse = `üé¨ **Avatar Video erstellt!**\n\nüìù **Text:** ${input.original_text || input.converted_text}\nüë§ **Benutzer:** ${input.user_name}\n‚úÖ **Status:** Video wird generiert\n\nüîó **Video URL:** ${apiResult.video_url}\n‚è±Ô∏è **Dauer:** ${apiResult.duration}\nüí∞ **Kosten:** ${apiResult.cost}\n\nüí° **N√§chste Schritte:** Video kann zu Social Media hochgeladen werden`;
    }
    break;

  case 'search':
    // Real Google Search result
    const query = input.original_text?.replace(/suche|finde|search/gi, '').trim() || 'KI';
    apiResult = {
      query: query,
      results: input.results || [],
      total_results: input.results?.length || 0
