    finalResponse = `üîç **Suchergebnisse f√ºr: ${query}**\n\nüë§ **Benutzer:** ${input.user_name}\n‚úÖ **Quellen:** Google\n\n**Google Ergebnisse:**\n${apiResult.results.map((r, i) => `‚Ä¢ **Ergebnis ${i+1}**\n  ${r.title || 'Kein Titel'}\n  ${r.snippet || 'Keine Beschreibung'}`).join('\n\n')}\n\nüí° **Tipp:** Du kannst auch sagen "Suche nach [Thema]"`;
    break;

  case 'weather':
    // Real Weather API result
    if (input.main) {
      apiResult = {
        city: input.name || 'Berlin',
        temperature: Math.round(input.main.temp) + '¬∞C',
        condition: input.weather?.[0]?.description || 'Unbekannt',
        humidity: input.main.humidity + '%',
        wind: Math.round(input.wind?.speed * 3.6) + ' km/h'
      };
      finalResponse = `üå§Ô∏è **Wetter in ${apiResult.city}**\n\nüë§ **Benutzer:** ${input.user_name}\nüå°Ô∏è **Temperatur:** ${apiResult.temperature}\n‚òÅÔ∏è **Bedingung:** ${apiResult.condition}\nüíß **Luftfeuchtigkeit:** ${apiResult.humidity}\nüí® **Wind:** ${apiResult.wind}\n\nüí° **Tipp:** Du kannst auch sagen "Wie ist das Wetter in [Stadt]?"`;
    }
    break;

  case 'memory':
