    apiResult = {
      memory_accessed: true,
      user_context: input.context
    };
    break;

  case 'help':
    // Help is already handled in text processor
    apiResult = {
      help_shown: true
    };
    break;

  default:
    // General conversation
    apiResult = {
      conversation_logged: true,
      context_updated: true
    };
    break;
}

return [{
